package httpserver_pkg;

import java.io.DataInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.net.ServerSocket;
import java.net.Socket;
import java.util.HashMap;
import java.util.Map;

import page.doccomp;
import page.index_html;

public class http_server {
  ServerSocket server;
  
  public http_server(int port) {
    try {
      server = new ServerSocket(port);
      System.out.println("server-start");
      while (true) {
        // wait next user
        Socket new_user = server.accept();
        new ConnectionThread(new_user).start();
      }
    } catch (IOException e) {
      e.printStackTrace();
    }
  }
  
  // ConnnectionThread类完成与一个Web浏览器的通信
  public static class ConnectionThread extends Thread {
    Socket user = null;
    
    public ConnectionThread(Socket user_socket) {
      user = user_socket;
    }
    
    public void run() {
      String destIP = user.getInetAddress().toString(); // 客户机IP地址
      int destport = user.getPort(); // 客户机端口号
      System.out.println("Connection: " + destIP + " :" + destport);
      try {
        
        String request = read_request(user.getInputStream());
        System.out.println("  request: "+request);
        String[] request_arr = request.split(" ");
        Map<String,String> header = read_header(user.getInputStream());
        
        // switch
        do {
          if (request_arr[1].equalsIgnoreCase("/doccomp")) {
            doccomp page =new doccomp(request, header,  user.getInputStream());
            user.getOutputStream().write(page.toString().getBytes("utf-8"));
            break;
          }          
          if (request_arr[1].equalsIgnoreCase("/echo")) {
            int bufflen = Integer.parseInt(header.get("Content-Length"));
            byte[] charbuff = new byte[bufflen];
            
            int len = user.getInputStream().read(charbuff, 0, bufflen);
            user.getOutputStream().write(
                "HTTP/1.1 200 OK \r\n pl: hello_world\r\n\r\n".getBytes());
            user.getOutputStream().write(charbuff, 0, len);
            break;
          }
          if (request_arr[1].equalsIgnoreCase("/download.rar")) {
            byte[] charbuff = " ".getBytes();
            user.getOutputStream().write(
                "HTTP/1.1 200 OK \r\n pl: hello_world\r\n\r\n".getBytes());
            int n=100000;
            while(n-->0)
              user.getOutputStream().write(charbuff);
            break;
          }
          if (true||request_arr[1].equalsIgnoreCase("/index.html")) {
            user.getOutputStream()
                .write(new index_html().toString().getBytes("utf-8"));
            break;
          }
        } while (false);
        // output.close();
      } catch (IOException e) {
        e.printStackTrace();
      }
      
      try {
        user.close();
      } catch (IOException e) {
        e.printStackTrace();
      }
    }
    
    @SuppressWarnings("deprecation")
    public String read_request(InputStream inputStream) {
      DataInputStream dis = new DataInputStream(inputStream);
      try {
        return dis.readLine();
      } catch (IOException e) {
        return null;
      }
    }
    
    @SuppressWarnings("deprecation")
    public Map<String,String> read_header(InputStream input) {
      DataInputStream dis = new DataInputStream(input);
      Map<String,String> ret = new HashMap<String,String>();
      String buff;
      try {
        while (true) {
          buff = dis.readLine();
          if (buff == null || buff.length() == 0) break;
          String[] key_val = buff.split(": ");
          if (key_val.length == 2) ret.put(key_val[0], key_val[1]);
        }
      } catch (IOException e) {}
      return ret;
    }
  }
  
  /**
   * @param args
   */
  public static void main(String[] args) {
    new http_server(80);
  }
  
}
